import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import WatchActions from "../components/WatchActions";

export default function SongWatch() {
  const { id } = useParams();

  const [song, setSong] = useState(null);
  const [showReplayToggle, setShowReplayToggle] = useState(false);
  const [autoReplay, setAutoReplay] = useState(false);

  const playerRef = useRef(null);
  const ytContainerRef = useRef(null);

  /* -------- FETCH SONG -------- */
  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/songs/${id}`)
      .then(res => res.json())
      .then(data => {
        setSong(data);
        setShowReplayToggle(false);
        setAutoReplay(false);
      });
  }, [id]);

  /* -------- LOAD YOUTUBE PLAYER -------- */
  useEffect(() => {
    if (!song) return;

    const createPlayer = () => {
      if (playerRef.current) return;

      playerRef.current = new window.YT.Player(ytContainerRef.current, {
        videoId: song.videoId,
        playerVars: {
          autoplay: 1,
          playsinline: 1,
          rel: 0,
          modestbranding: 1,
        },
        events: {
          onStateChange: (event) => {
            if (event.data === window.YT.PlayerState.ENDED) {
              setShowReplayToggle(true);

              if (autoReplay) {
                playerRef.current.seekTo(0);
                playerRef.current.playVideo();
              }
            }
          },
        },
      });
    };

    if (!window.YT) {
      const tag = document.createElement("script");
      tag.src = "https://www.youtube.com/iframe_api";
      document.body.appendChild(tag);
      window.onYouTubeIframeAPIReady = createPlayer;
    } else {
      createPlayer();
    }
  }, [song, autoReplay]);

  /* -------- CLEANUP -------- */
  useEffect(() => {
    return () => {
      if (playerRef.current?.destroy) {
        playerRef.current.destroy();
        playerRef.current = null;
      }
    };
  }, []);

  if (!song) return <h2 style={{ padding: 40 }}>Loading song...</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* -------- PLAYER (UNCHANGED LAYOUT) -------- */}
        <div className="video-wrapper">
          <div ref={ytContainerRef} />
        </div>

        {/* -------- REPLAY TOGGLE (AFTER END) -------- */}
        {showReplayToggle && (
          <div style={{ marginTop: 12, display: "flex", gap: 10 }}>
            <label style={{ cursor: "pointer" }}>
              <input
                type="checkbox"
                checked={autoReplay}
                onChange={(e) => setAutoReplay(e.target.checked)}
                style={{ marginRight: 6 }}
              />
              Replay automatically
            </label>

            {!autoReplay && (
              <button
                onClick={() => {
                  playerRef.current.seekTo(0);
                  playerRef.current.playVideo();
                }}
              >
                â–¶ Replay now
              </button>
            )}
          </div>
        )}

        {/* -------- HEADER -------- */}
        <div className="watch-header">
          <h1 className="video-title">{song.title}</h1>

          <WatchActions
            itemId={song._id}
            type="song"
            videoUrl={`https://www.youtube.com/watch?v=${song.videoId}`}
          />
        </div>

        {/* -------- META -------- */}
        <div className="video-meta">
          <span>{song.host}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
