import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import PageTransition from "../PageTransition";
import SearchBar from "../components/SearchBar";

export default function Podcasts() {
  const [podcasts, setPodcasts] = useState([]);
  const [query, setQuery] = useState("");

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/podcasts`)
      .then((res) => res.json())
      .then((data) => setPodcasts(data));
  }, []);

  const filtered = podcasts.filter((p) =>
    p.title.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <PageTransition>
      <Container>
        <div className="page-header">
          <h1>Podcasts</h1>
          <SearchBar
            value={query}
            onChange={setQuery}
            placeholder="Search podcasts..."
          />
        </div>

        <div className="video-grid">
          {filtered.map((p) => (
            <article key={p._id} className="video-card">
              {/* ✅ ROUTING BY _id */}
              <Link to={`/podcasts/${p._id}`}>
                <div className="video-wrapper">
                  <img
                    src={`https://img.youtube.com/vi/${p.videoId}/hqdefault.jpg`}
                    alt={p.title}
                  />
                  <span className="play-overlay">▶</span>
                </div>
              </Link>

              <h2 className="video-title">{p.title}</h2>

              <div className="video-meta">
                {p.host || p.author || "Podcast"}
              </div>

              
          
            </article>
          ))}
        </div>
      </Container>
    </PageTransition>
  );
}
