import { Link, Outlet } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function AdminLayout() {
  const { logout } = useAuth();

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* Sidebar */}
      <aside
        style={{
          width: "220px",
          background: "#111",
          color: "#fff",
          padding: "20px",
        }}
      >
        <h3>Admin Panel</h3>

        <nav style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <Link to="/admin/dashboard" style={{ color: "#fff" }}>
            Dashboard
          </Link>
          <Link to="/admin/videos" style={{ color: "#fff" }}>
            Videos
          </Link>
          <Link to="/admin/songs" style={{ color: "#fff" }}>
            Songs
          </Link>
          <Link to="/admin/podcasts" style={{ color: "#fff" }}>
            Podcasts
          </Link>
        </nav>

        <button
          onClick={logout}
          style={{ marginTop: "20px", padding: "6px" }}
        >
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main style={{ flex: 1, padding: "30px" }}>
        <Outlet />
      </main>
    </div>
  );
}
