import React, { useEffect } from "react";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function Posts() {

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://www.instagram.com/embed.js";
    script.async = true;

    script.onload = () => {
      if (window.instgrm) window.instgrm.Embeds.process();
    };

    document.body.appendChild(script);
  }, []);

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        <h1> Posts</h1>

        {/* EVERYTHING MUST STAY INSIDE THIS GRID */}
        <div className="insta-grid">

          <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DS7GOdFEtoZ/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

          <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DSroOQekp0S/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

          <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DSmNgzJj-ue/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

          <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DSZCgfmj_cd/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

               <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DSW1VO7FCSd/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

           <div className="insta-item">
            <div
              dangerouslySetInnerHTML={{
                __html: `
                  <blockquote 
                    class="instagram-media" 
                    data-instgrm-permalink="https://www.instagram.com/p/DSNAkdZlDUI/"
                    data-instgrm-version="14"
                    data-instgrm-captioned>
                  </blockquote>
                `,
              }}
            />
          </div>

        </div>

        <style>
          {`
            .insta-grid{
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 32px;
}

/* remove aspect-ratio */
.insta-item{
  width: 100%;
}

/* allow embed to size naturally */
.insta-item .instagram-media{
  width: 100% !important;
  margin: 0 !important;
}


            /* responsiveness */
            @media(max-width: 992px){
              .insta-grid{
                grid-template-columns: repeat(2, 1fr);
              }
            }

            @media(max-width: 600px){
              .insta-grid{
                grid-template-columns: 1fr;
              }
            }
          `}
        </style>

      </Container>
    </PageTransition>
  );
}
