import PageTransition from "../PageTransition";

export default function Blog() {
  const posts = [
    {
      date: "18/12/2025",
      upload: "18/12/2025",
      text: "இந்த காணொளியில் திமுக கட்சியினரின் 10 பிரபலமான மோசடிகளை அவர்கள் பெயருக்கு ஏற்ற கிண்டலாக விளக்கிகிறோம்.",
      link: "https://youtube.com/shorts/UobU3mvxat4?si=jV0LPz4Hn6RnMU15"
    },
    {
      date: "19/12/2025",
      upload: "19/12/2025",
      text: "இந்த காணொளியில், மோசடி செய்தபவர்கள் அவர்கள் செய்த மோசடிகளுக்கு ஏற்ப கிண்டலாகத் தேர்ந்தெடுத்துள்ளேன், மேலும் பார்வையாளர்களுக்கு வித்தியாசமான வகையைச் சேர்க்கும் வகையில் இந்த உள்ளடக்கத்தை அமைத்துள்ளேன்.",
      link: "https://youtube.com/shorts/QW3eLSwb3tk?si=LRkgzmGG0w5-kFfS"
    },
 
  ];

  // extract video id from normal + shorts + youtu.be
  const getVideoId = (url) => {
    try {
      if (url.includes("shorts/")) {
        return url.split("shorts/")[1].split("?")[0];
      }
      if (url.includes("watch?v=")) {
        return url.split("watch?v=")[1].split("&")[0];
      }
      if (url.includes("youtu.be/")) {
        return url.split("youtu.be/")[1].split("?")[0];
      }
    } catch {}
    return "";
  };

  return (
    <PageTransition>
      <section className="blog-hero">
  <h1>BLOG PAGE</h1>
  <p className="blog-subtitle">
    Editorial insights and perspectives.
  </p>
</section>

<section className="blog-container">
  {posts.map((p, i) => {
    const vid = getVideoId(p.link);
    const thumb = `https://img.youtube.com/vi/${vid}/hqdefault.jpg`;

    return (
      <div key={i} className="blog-card">

  {/* TEXT FIRST */}
  <p className="blog-text">{p.text}</p>

  {/* THUMBNAIL */}
  {vid && (
    <a href={p.link} target="_blank" rel="noopener noreferrer">
      <img src={thumb} className="blog-thumb" alt="YouTube thumbnail" />
    </a>
  )}

  {/* LINK */}
  <p className="blog-link">
    <strong>Link:</strong>{" "}
    <a href={p.link} target="_blank" rel="noopener noreferrer">
      {p.link}
    </a>
  </p>

  {/* NOW DATE AT THE BOTTOM */}
  <p className="meta"><strong>Date:</strong> {p.date}</p>
  <p className="meta"><strong>Uploaded:</strong> {p.upload}</p>

</div>

    );
  })}
</section>

    </PageTransition>
  );
}
