import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import SearchBar from "../components/SearchBar";
import { Link } from "react-router-dom";

export default function Podcasts() {
  const [podcasts, setPodcasts] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);

  const formatDate = (date) =>
    new Date(date).toLocaleDateString("en-IN", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

  // âœ… FETCH FROM BACKEND (MongoDB)
  useEffect(() => {
    setLoading(true);

    fetch(`${process.env.REACT_APP_API_URL}/api/podcasts`)
      .then((res) => res.json())
      .then((data) => {
        setPodcasts(data || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch podcasts", err);
        setLoading(false);
      });
  }, []);

  // âœ… SEARCH BY TITLE
  const filtered = podcasts.filter((p) =>
    p.title.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <PageTransition>
      <Container>
        <div className="page-header">
          <h1>Podcasts</h1>

          <div className="page-search">
            <SearchBar
              value={query}
              onChange={setQuery}
              placeholder="Search podcasts..."
            />
          </div>
        </div>

        {/* Loading skeleton */}
        {loading && (
          <div className="video-grid">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="video-card skeleton">
                <div className="video-wrapper shimmer-box" />
                <div className="video-title shimmer-line" />
              </div>
            ))}
          </div>
        )}

        {!loading && filtered.length === 0 && (
          <div className="no-results">ðŸ˜• No results found</div>
        )}

        {!loading && filtered.length > 0 && (
          <div className="video-grid">
            {filtered.map((p) => (
              <article key={p._id} className="video-card">
                <Link
                  to={`/podcasts/${p.videoId}`}
                  state={{
                    title: p.title,
                    author: p.author,
                    date: formatDate(p.date),
                    embed: p.embed,
                  }}
                >
                  <div className="video-wrapper">
                   <iframe
  width="100%"
  height="420"
  src={`https://www.youtube.com/embed/${video.videoId}`}
  title={video.title}
  frameBorder="0"
  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
  allowFullScreen
/>

                  </div>
                </Link>

                <h2 className="video-title">{p.title}</h2>

                <div className="video-meta">
                  <span>{p.author}</span>
                  <span>â€”</span>
                  <span>{formatDate(p.date)}</span>
                </div>
              </article>
            ))}
          </div>
        )}
      </Container>
    </PageTransition>
  );
}
