import { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function PodcastWatch() {
  const { id } = useParams();
  const [podcast, setPodcast] = useState(null);
  const [loading, setLoading] = useState(true);

  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);
  const shareRef = useRef();

  useEffect(() => {
    fetch(`/api/podcasts/${id}`)
      .then(res => res.json())
      .then(data => setPodcast(data))
      .finally(() => setLoading(false));
  }, [id]);

  useEffect(() => {
    const saved = localStorage.getItem("podcast-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("podcast-reactions", JSON.stringify(reactions));
  }, [reactions]);

  useEffect(() => {
    const handler = e => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  if (loading) return <h2 style={{ padding: 40 }}>Loading podcast...</h2>;
  if (!podcast) return <h2 style={{ padding: 40 }}>Podcast not found</h2>;

  const videoUrl = `https://www.youtube.com/watch?v=${podcast.videoId}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${podcast.videoId}`}
            title={podcast.title}
            allowFullScreen
          />
        </div>

        <div className="watch-header">
          <h1 className="video-title">{podcast.title}</h1>

          <div className="video-actions">
            <button
              className={`action-btn ${reactions[id] === "like" ? "active like" : ""}`}
              onClick={() =>
                setReactions(p => ({ ...p, [id]: p[id] === "like" ? null : "like" }))
              }
            >
              üëç Like
            </button>

            <button
              className={`action-btn ${reactions[id] === "dislike" ? "active dislike" : ""}`}
              onClick={() =>
                setReactions(p => ({ ...p, [id]: p[id] === "dislike" ? null : "dislike" }))
              }
            >
              üëé Dislike
            </button>

            <div className="share-container" ref={shareRef}>
              <button className="action-btn" onClick={() => setOpenShare(p => !p)}>
                üîó Share
              </button>

              {openShare && (
                <div className="share-menu">
                  <a href={`https://wa.me/?text=${encodeURIComponent(videoUrl)}`} target="_blank" rel="noreferrer">WhatsApp</a>
                  <a href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(videoUrl)}`} target="_blank" rel="noreferrer">X</a>
                  <a href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(videoUrl)}`} target="_blank" rel="noreferrer">Facebook</a>
                  <button onClick={() => navigator.clipboard.writeText(videoUrl)}>Copy Link</button>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="video-meta">
          <span>{podcast.host}</span>
          <span>‚Äî</span>
          <span>
            {podcast.youtubePublishedAt
              ? new Date(podcast.youtubePublishedAt).toLocaleDateString()
              : ""}
          </span>
        </div>
      </Container>
    </PageTransition>
  );
}
