import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import SearchBar from "../components/SearchBar";
import { Link } from "react-router-dom";

export default function Videos() {
  const API_KEY = "AIzaSyBcDen3i7LkL88D7RRPzRvvGxSCVKcSTKc";
  const CHANNEL_ID = "UCReZMtVP4QYpV9PwfR50dHg";

  const [videos, setVideos] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);

  const formatDate = (iso) =>
    new Date(iso).toLocaleDateString("en-IN", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

  useEffect(() => {
    setLoading(true);

    fetch(
      `https://www.googleapis.com/youtube/v3/channels?part=contentDetails&id=${CHANNEL_ID}&key=${API_KEY}`
    )
      .then((res) => res.json())
      .then((data) => {
        const uploads =
          data?.items?.[0]?.contentDetails?.relatedPlaylists?.uploads;

        if (!uploads) {
          setVideos([]);
          setLoading(false);
          return;
        }

        fetch(
          `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&playlistId=${uploads}&maxResults=50&key=${API_KEY}`
        )
          .then((res) => res.json())
          .then((data) => {
            setVideos(data.items || []);
            setLoading(false);
          });
      });
  }, [API_KEY, CHANNEL_ID]);

  const filtered = videos.filter((v) =>
    v?.snippet?.title?.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <PageTransition>
      <Container>
        <div className="page-header">
          <h1>Videos</h1>

          <div className="page-search">
            <SearchBar
              value={query}
              onChange={setQuery}
              placeholder="Search videos..."
            />
          </div>
        </div>

        {loading && (
          <div className="video-grid">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="video-card skeleton">
                <div className="video-wrapper shimmer-box" />
                <div className="video-title shimmer-line" />
              </div>
            ))}
          </div>
        )}

        {!loading && filtered.length === 0 && (
          <div className="no-results">ðŸ˜• No results found</div>
        )}

        {!loading && filtered.length > 0 && (
          <div className="video-grid">
            {filtered.map((v) => {
              const s = v.snippet;
              const videoId = s?.resourceId?.videoId;
              const embed = `https://www.youtube.com/embed/${videoId}`;

              return (
                <article key={videoId} className="video-card">

                  {/* CLICK â†’ Watch page */}
                  <Link
                    to={`/videos/${videoId}`}
                    className="video-wrapper-link"
                    state={{
                      title: s?.title,
                      author: s?.channelTitle,
                      date: formatDate(s?.publishedAt),
                    }}
                  >
                    <div
                      className="video-wrapper"
                      onMouseEnter={(e) => {
                        const iframe =
                          e.currentTarget.querySelector("iframe");
                        iframe.src = embed + "?autoplay=1&mute=1";
                      }}
                      onMouseLeave={(e) => {
                        const iframe =
                          e.currentTarget.querySelector("iframe");
                        iframe.src = embed;
                      }}
                    >
                      <iframe
                        src={embed}
                        title={s?.title}
                        loading="lazy"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        referrerPolicy="strict-origin-when-cross-origin"
                        allowFullScreen
                        style={{ pointerEvents: "none" }}
                      />
                    </div>
                  </Link>

                  <h2 className="video-title">
                    <Link
                      to={`/videos/${videoId}`}
                      state={{
                        title: s?.title,
                        author: s?.channelTitle,
                        date: formatDate(s?.publishedAt),
                      }}
                      style={{ color: "white", textDecoration: "none" }}
                    >
                      {s?.title}
                    </Link>
                  </h2>

                  <div className="video-meta">
                    <span>{s?.channelTitle}</span>
                    <span>â€”</span>
                    <span>{formatDate(s?.publishedAt)}</span>
                  </div>
                </article>
              );
            })}
          </div>
        )}
      </Container>
    </PageTransition>
  );
}
