import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import SearchBar from "../components/SearchBar";
import { Link } from "react-router-dom";

export default function Songs() {
  const [songs, setSongs] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);

    fetch(`${process.env.REACT_APP_API_URL}/api/songs`)
      .then((res) => res.json())
      .then((data) => {
        setSongs(data || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch songs", err);
        setLoading(false);
      });
  }, []);

  const filtered = songs.filter((v) =>
    v?.title?.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <PageTransition>
      <Container>
        <div className="page-header">
          <h1>Songs</h1>

          <div className="page-search">
            <SearchBar
              value={query}
              onChange={setQuery}
              placeholder="Search songs..."
            />
          </div>
        </div>

        {loading && <div className="no-results">Loading songs...</div>}

        {!loading && filtered.length === 0 && (
          <div className="no-results">ðŸ˜• No results found</div>
        )}

        <div className="video-grid">
          {filtered.map((v) => (
            <article key={v._id} className="video-card">
              <Link to={`/songs/${v.videoId}`} className="video-wrapper-link">
                <div
                  className="video-wrapper"
                  onMouseEnter={(e) => {
                    const iframe = e.currentTarget.querySelector("iframe");
                    iframe.src = v.embed + "?autoplay=1&mute=1";
                  }}
                  onMouseLeave={(e) => {
                    const iframe = e.currentTarget.querySelector("iframe");
                    iframe.src = v.embed;
                  }}
                >
                  <iframe
                    src={v.embed}
                    title={v.title}
                    loading="lazy"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    style={{ pointerEvents: "none" }}
                  />
                </div>
              </Link>

              <h2 className="video-title">
                <Link
                  to={`/songs/${v.videoId}`}
                  style={{ color: "white", textDecoration: "none" }}
                >
                  {v.title}
                </Link>
              </h2>

              <div className="video-meta">
                <span>{v.author}</span>
                <span>â€”</span>
                <span>{v.date}</span>
              </div>
            </article>
          ))}
        </div>
      </Container>
    </PageTransition>
  );
}
