import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";
import WatchActions from "../components/WatchActions";

export default function VideoWatch() {
  const { id } = useParams();
  const [video, setVideo] = useState(null);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/videos/${id}`)
      .then((res) => res.json())
      .then((data) => setVideo(data));
  }, [id]);

  if (!video) return <h2 style={{ padding: 40 }}>Loading video...</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* PLAYER */}
        <div className="video-wrapper">
          <iframe
  src={`https://www.youtube.com/embed/${video.videoId}?autoplay=1&mute=1&playsinline=1`}
  title={video.title}
  allow="autoplay; encrypted-media"
  allowFullScreen
/>

        </div>

        {/* HEADER â€” CSS UNCHANGED */}
        <div className="watch-header">
          <h1 className="video-title">{video.title}</h1>

          <WatchActions
            itemId={video._id}
            type="video"
            videoUrl={`https://www.youtube.com/watch?v=${video.videoId}`}
          />
        </div>

        {/* META */}
        <div className="video-meta">
          <span>{video.host}</span>
          
          
        </div>
      </Container>
    </PageTransition>
  );
}
