import { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function SongWatch() {
  const { id } = useParams();
  const [song, setSong] = useState(null);

  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);
  const shareRef = useRef(null);

  // Fetch song by Mongo _id
  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/songs/${id}`)
      .then((res) => res.json())
      .then((data) => setSong(data));
  }, [id]);

  // Load reactions
  useEffect(() => {
    const saved = localStorage.getItem("song-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  // Save reactions
  useEffect(() => {
    localStorage.setItem("song-reactions", JSON.stringify(reactions));
  }, [reactions]);

  // Close share popup on outside click
  useEffect(() => {
    const handler = (e) => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  if (!song) {
    return <h2 style={{ padding: 40 }}>Loading song...</h2>;
  }

  const videoUrl = `https://www.youtube.com/watch?v=${song.videoId}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* PLAYER */}
        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${song.videoId}`}
            title={song.title}
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>

        {/* TITLE + ACTIONS (OLD LAYOUT PRESERVED) */}
        <div className="watch-header">
          <h1 className="video-title">{song.title}</h1>

          <div className="video-actions">
            <button
              className={`action-btn ${
                reactions[id] === "like" ? "active like" : ""
              }`}
              onClick={() =>
                setReactions((p) => ({
                  ...p,
                  [id]: p[id] === "like" ? null : "like",
                }))
              }
            >
              üëç Like
            </button>

            <button
              className={`action-btn ${
                reactions[id] === "dislike" ? "active dislike" : ""
              }`}
              onClick={() =>
                setReactions((p) => ({
                  ...p,
                  [id]: p[id] === "dislike" ? null : "dislike",
                }))
              }
            >
              üëé Dislike
            </button>

            {/* SHARE */}
            <div className="share-container" ref={shareRef}>
              <button
                className="action-btn"
                onClick={() => setOpenShare((p) => !p)}
              >
                üîó Share
              </button>

              {openShare && (
                <div className="share-menu pro">
                  <a
                    href={`https://wa.me/?text=${encodeURIComponent(videoUrl)}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    WhatsApp
                  </a>
                  <a
                    href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(
                      videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    X
                  </a>
                  <a
                    href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(
                      videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    Facebook
                  </a>
                  <button
                    onClick={() => navigator.clipboard.writeText(videoUrl)}
                  >
                    Copy Link
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* META (STYLE PRESERVED) */}
        <div className="video-meta">
          <span>{song.artist}</span>
          <span>‚Äî</span>
          <span>
            {song.youtubePublishedAt
              ? new Date(song.youtubePublishedAt).toLocaleDateString()
              : ""}
          </span>
        </div>
      </Container>
    </PageTransition>
  );
}
