import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import SearchBar from "../components/SearchBar";
import { Link } from "react-router-dom";

export default function Songs() {
  const [songs, setSongs] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);

  const formatDate = (date) =>
    new Date(date).toLocaleDateString("en-IN", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

  // âœ… FETCH FROM BACKEND (MongoDB)
  useEffect(() => {
    setLoading(true);

    fetch(`${process.env.REACT_APP_API_URL}/api/songs`)
      .then((res) => res.json())
      .then((data) => {
        setSongs(data || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch songs", err);
        setLoading(false);
      });
  }, []);

  // âœ… SEARCH BY TITLE
  const filtered = songs.filter((s) =>
    s.title.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <PageTransition>
      <Container>
        <div className="page-header">
          <h1>Songs</h1>

          <div className="page-search">
            <SearchBar
              value={query}
              onChange={setQuery}
              placeholder="Search songs..."
            />
          </div>
        </div>

        {/* Loading skeleton */}
        {loading && (
          <div className="video-grid">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="video-card skeleton">
                <div className="video-wrapper shimmer-box" />
                <div className="video-title shimmer-line" />
              </div>
            ))}
          </div>
        )}

        {!loading && filtered.length === 0 && (
          <div className="no-results">ðŸ˜• No results found</div>
        )}

        {!loading && filtered.length > 0 && (
          <div className="video-grid">
            {filtered.map((s) => (
              <article key={s._id} className="video-card">
                <Link
                  to={`/songs/${s.videoId}`}
                  state={{
                    title: s.title,
                    author: s.author,
                    date: formatDate(s.date),
                    embed: s.embed,
                  }}
                >
                  <div className="video-wrapper">
                    <iframe
                      src={s.embed}
                      title={s.title}
                      loading="lazy"
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                      style={{ pointerEvents: "none" }}
                    />
                  </div>
                </Link>

                <h2 className="video-title">{s.title}</h2>

                <div className="video-meta">
                  <span>{s.author}</span>
                  <span>â€”</span>
                  <span>{formatDate(s.date)}</span>
                </div>
              </article>
            ))}
          </div>
        )}
      </Container>
    </PageTransition>
  );
}
