import { useEffect, useRef, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import WatchActions from "../components/WatchActions";

export default function SongWatch() {
  const { id } = useParams();

  const [song, setSong] = useState(null);
  const [ended, setEnded] = useState(false);
  const [related, setRelated] = useState([]);

  const playerRef = useRef(null);

  /* ---------------- FETCH CURRENT SONG ---------------- */
  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/songs/${id}`)
      .then((res) => res.json())
      .then((data) => setSong(data));
  }, [id]);

  /* ---------------- LOAD YOUTUBE PLAYER API ---------------- */
  useEffect(() => {
    if (!song) return;

    // Load API once
    if (!window.YT) {
      const tag = document.createElement("script");
      tag.src = "https://www.youtube.com/iframe_api";
      document.body.appendChild(tag);
    }

    window.onYouTubeIframeAPIReady = () => {
      playerRef.current = new window.YT.Player("yt-player", {
        videoId: song.videoId,
        playerVars: {
          autoplay: 1,
          playsinline: 1,
          rel: 0,
          modestbranding: 1,
        },
        events: {
          onStateChange: (event) => {
            if (event.data === window.YT.PlayerState.ENDED) {
              handleVideoEnd();
            }
          },
        },
      });
    };
  }, [song]);

  /* ---------------- HANDLE VIDEO END ---------------- */
  const handleVideoEnd = () => {
    setEnded(true);

    // Fetch same-channel videos
    fetch(`${process.env.REACT_APP_API_URL}/api/songs`)
      .then((res) => res.json())
      .then((allSongs) => {
        const sameChannel = allSongs.filter(
          (s) => s.host === song.host && s._id !== song._id
        );
        setRelated(sameChannel);
      });
  };

  if (!song) return <h2 style={{ padding: 40 }}>Loading song...</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* ---------------- PLAYER / END SCREEN ---------------- */}
        <div className="video-wrapper">
          {!ended ? (
            <div id="yt-player" />
          ) : (
            <div className="custom-end-screen">
              <h2>More from {song.host}</h2>

              <div className="related-grid">
                {related.map((item) => (
                  <Link
                    key={item._id}
                    to={`/songs/${item._id}`}
                    className="related-card"
                  >
                    <img
                      src={`https://img.youtube.com/vi/${item.videoId}/hqdefault.jpg`}
                      alt={item.title}
                    />
                    <p>{item.title}</p>
                  </Link>
                ))}
              </div>

              <div className="end-actions">
                <button
                  onClick={() => {
                    setEnded(false);
                    playerRef.current.playVideo();
                  }}
                >
                  ▶ Replay
                </button>

                <Link to="/songs">
                  <button>⬅ Back to Songs</button>
                </Link>
              </div>
            </div>
          )}
        </div>

        {/* ---------------- HEADER ---------------- */}
        <div className="watch-header">
          <h1 className="video-title">{song.title}</h1>

          <WatchActions
            itemId={song._id}
            type="song"
            videoUrl={`https://www.youtube.com/watch?v=${song.videoId}`}
          />
        </div>

        {/* ---------------- META ---------------- */}
        <div className="video-meta">
          <span>{song.host}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
