import PageTransition from "../PageTransition";
import { useEffect } from "react";

import pic1 from "../assets/pic1.jpeg";
import pic2 from "../assets/pic2.jpeg";
import pic3 from "../assets/pic3.jpeg";
export default function About() {

  useEffect(() => {
  const elements = document.querySelectorAll(".reveal, .reveal-left");

  const observer = new IntersectionObserver(
    entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add("show");
        }
      });
    },
    { threshold: 0.2 }
  );

  elements.forEach(el => observer.observe(el));

  return () => observer.disconnect();
}, []);

  return (
    <PageTransition>
    <section className="hero">
      <h1>ABOUT US</h1>
      <div>

    {/* === Section 1 === */}
  <div className="about-block reveal">
    
    <div className="about-text">
      <h2 className="about-subtitle" style={{fontSize:"30px", textAlign:'center'}} >Political Parody</h2>
      <p style={{fontSize:"22px"}}>
        <r>You can enjoy a variety of everyday troll content about the DMK party here,
        along with a satirical explanation of the party's drawbacks and why we don't need it. </r>
      </p>
    </div>

    <div className="about-photo">
      <div className="photo-frame">
        <img src={pic1} alt="Political satire" />
      </div>
    </div>
  </div>


 {/* === Section 2 (IMAGE LEFT) === */}
<div className="about-block reverse reveal-left">

  {/* TEXT FIRST in the DOM */}
  <div className="about-text">
    <h2 className="about-subtitle" style={{fontSize:"30px", textAlign:'center'}}>Campaign Carnival</h2>
    <p style={{fontSize:"22px"}}> <r>
      Welcome to the circus of democracy, where career politicians juggle your tax dollars like flaming
       chainsaws while promising the moon on a silver platter. They campaign on "change" every four years, 
      only to deliver the same tired script of corporate bailouts, endless wars, and virtue-signaling photo ops </r>
    </p>
  </div>

  {/* IMAGE SECOND */}
  <div className="about-photo">
    <div className="photo-frame">
      <img src={pic2} alt="Campaign Carnival" />
    </div>
  </div>

</div>



  {/* === Section 3 === */}
  <div className="about-block reveal">

    <div className="about-text">
      <h2 className="about-subtitle" style={{fontSize:"30px", textAlign:'center'}}>Outrage Economy</h2>
      <p style={{fontSize:"22px"}}>
       <r> Who needs competence when you've got charisma and a Super PAC? Pull up a seat, grab some popcorn, and
        watch as they turn your outrage into their next election fundâ€”because in politics, the joke's always on you.</r>
      </p>
    </div>

    <div className="about-photo">
      <div className="photo-frame">
        <img src={pic3} alt="Outrage economics satire" />
      </div>
    </div>
  </div>

</div>
    </section>
    </PageTransition>
  );
}
