import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";

export default function VideoCarousel() {
  /* üîí ALL HOOKS FIRST ‚Äî NO RETURNS ABOVE THIS */

  const [videos, setVideos] = useState([]);
  const [index, setIndex] = useState(1);
  const [speed, setSpeed] = useState(".6s");

  /* Fetch latest videos from backend */
  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/youtube/latest");
        const data = await res.json();
        setVideos(data.slice(0, 3).map(v => v.id));

      } catch (err) {
        console.error("Backend YouTube fetch failed", err);
      }
    };

    fetchLatest();
  }, []);

  /* Carousel navigation */
  const next = useCallback(() => {
    setIndex((i) => i + 1);
  }, []);

  const prev = () => {
    setIndex((i) => i - 1);
  };

  /* Auto slide */
  useEffect(() => {
    if (videos.length === 0) return;

    const timer = setInterval(next, 4500);
    return () => clearInterval(timer);
  }, [next, videos.length]);

  /* Infinite loop correction */
  useEffect(() => {
    if (videos.length === 0) return;

    const slidesLength = videos.length + 2;

    if (index === slidesLength - 1) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(1);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }

    if (index === 0) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(slidesLength - 2);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }
  }, [index, videos.length]);

  /* Build slides AFTER hooks */
  const slides =
    videos.length > 0
      ? [videos[videos.length - 1], ...videos, videos[0]]
      : [];

  /* ‚úÖ Conditional render INSIDE JSX */
  if (videos.length === 0) {
    return null;
  }

  return (
    <section className="carousel-section">
      <h2 className="carousel-title" style={{ fontWeight: "bold" }}>
        Latest Videos
      </h2>

      <div className="simple-carousel">
        <button className="nav left" onClick={prev}>‚ùÆ</button>

        <div className="track-wrapper">
          <div
            className="track"
            style={{
              transform: `translateX(-${index * 100}%)`,
              transition: speed,
            }}
          >
            {slides.map((id, i) => (
              <div className="slide" key={i}>
                <Link to={`/videos/${id}`} className="thumb-link">
                  <img
                    src={`https://img.youtube.com/vi/${id}/hqdefault.jpg`}
                    alt="video thumbnail"
                    className="thumb-img"
                  />
                  <span className="play-overlay">‚ñ∂</span>
                </Link>
              </div>
            ))}
          </div>
        </div>

        <button className="nav right" onClick={next}>‚ùØ</button>
      </div>
    </section>
  );
}
