import React, { useEffect, useState, useRef } from "react";
import { useParams, useLocation } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function VideoWatch() {
  const { id } = useParams(); // id = videoId
  const location = useLocation();
  const { title, author, date } = location.state || {};

  const shareRef = useRef();
  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("video-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("video-reactions", JSON.stringify(reactions));
  }, [reactions]);

  const handleLike = () =>
    setReactions((p) => ({ ...p, [id]: p[id] === "like" ? null : "like" }));

  const handleDislike = () =>
    setReactions((p) => ({ ...p, [id]: p[id] === "dislike" ? null : "dislike" }));

  const toggleShare = () => setOpenShare((p) => !p);

  useEffect(() => {
    const handler = (e) => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  const videoUrl = `https://www.youtube.com/watch?v=${id}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${id}`}
            title={title || "Video"}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>

        <h1 className="video-title">{title || "Video"}</h1>

        <div className="video-meta">
          <span>{author || "Channel"}</span>
          <span>â€”</span>
          <span>{date || ""}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
