import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";

export default function VideoCarousel() {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/youtube/latest");
        const data = await res.json();
        setVideos(data.map((v) => v.id));
      } catch (err) {
        console.error("Backend YouTube fetch failed", err);
      }
    };

    fetchLatest();
  }, []);

  if (videos.length === 0) return null;

  /* ===== Carousel logic (unchanged) ===== */

  const slides = [videos[videos.length - 1], ...videos, videos[0]];
  const [index, setIndex] = useState(1);
  const [speed, setSpeed] = useState(".6s");

  const next = useCallback(() => setIndex((i) => i + 1), []);
  const prev = () => setIndex((i) => i - 1);

  useEffect(() => {
    const timer = setInterval(next, 4500);
    return () => clearInterval(timer);
  }, [next]);

  useEffect(() => {
    if (index === slides.length - 1) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(1);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }

    if (index === 0) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(slides.length - 2);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }
  }, [index, slides.length]);

  return (
    <section className="carousel-section">
      <h2 className="carousel-title">Latest Videos</h2>

      <div className="simple-carousel">
        <button className="nav left" onClick={prev}>❮</button>

        <div className="track-wrapper">
          <div
            className="track"
            style={{
              transform: `translateX(-${index * 100}%)`,
              transition: speed,
            }}
          >
            {slides.map((id, i) => (
              <div className="slide" key={i}>
                <Link to={`/videos/${id}`} className="thumb-link">
                  <img
                    src={`https://img.youtube.com/vi/${id}/hqdefault.jpg`}
                    alt="video thumbnail"
                  />
                  <span className="play-overlay">▶</span>
                </Link>
              </div>
            ))}
          </div>
        </div>

        <button className="nav right" onClick={next}>❯</button>
      </div>
    </section>
  );
}
