import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function PodcastWatch() {
  const { id } = useParams();
  const [podcast, setPodcast] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/api/podcasts/${id}`)
      .then((res) => res.json())
      .then((data) => setPodcast(data))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <h2 style={{ padding: 40 }}>Loading podcast...</h2>;
  if (!podcast) return <h2 style={{ padding: 40 }}>Podcast not found</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* PLAYER */}
        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${podcast.videoId}`}
            title={podcast.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>

        {/* META */}
        <h1 className="video-title">{podcast.title}</h1>

        <div className="video-meta">
          <span>{podcast.host}</span>
          <span>â€”</span>
          <span>
            {podcast.youtubePublishedAt
              ? new Date(podcast.youtubePublishedAt).toLocaleDateString()
              : ""}
          </span>
        </div>
      </Container>
    </PageTransition>
  );
}
