import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";
import WatchActions from "../components/WatchActions";

export default function PodcastWatch() {
  const { id } = useParams();
  const [podcast, setPodcast] = useState(null);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/podcasts/${id}`)
      .then((res) => res.json())
      .then((data) => setPodcast(data));
  }, [id]);

  if (!podcast) return <h2 style={{ padding: 40 }}>Loading podcast...</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* PLAYER */}
        <div className="video-wrapper">

          <iframe
            src={`https://www.youtube.com/embed/${podcast.videoId}?autoplay=1&playsinline=1`}
            allow="autoplay; encrypted-media"
            allowFullScreen
          />

        </div>

        {/* HEADER â€” CSS UNCHANGED */}
        <div className="watch-header">
          <h1 className="video-title">{podcast.title}</h1>

          <WatchActions
            itemId={podcast._id}
            type="podcast"
            videoUrl={`https://www.youtube.com/watch?v=${podcast.videoId}`}
          />
        </div>

        {/* META */}
        <div className="video-meta">
          <span>{podcast.host}</span>
          
          
        </div>
      </Container>
    </PageTransition>
  );
}
