import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";


const fallbackVideos = [
  "up4TuGdM5SQ",
  "0hjqWmnUDWo",
  "yvhI98yGzMg",
  "-mjCAtjN0MM",
  "y8WUI2ZmTlw"
];

export default function VideoCarousel() {
  const [videos, setVideos] = useState([]);

 
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const apiKey = process.env.REACT_APP_YT_API_KEY;
        const channelId = process.env.REACT_APP_CHANNEL_ID;

        const channelRes = await fetch(
          `https://www.googleapis.com/youtube/v3/channels?part=contentDetails&id=${channelId}&key=${apiKey}`
        );
        const channelData = await channelRes.json();

        const uploadsId =
          channelData.items[0].contentDetails.relatedPlaylists.uploads;

        let collected = [];
        let nextPageToken = "";
        let safety = 0;

        while (collected.length < 3 && safety < 5) {
          const playlistRes = await fetch(
            `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&playlistId=${uploadsId}&maxResults=10&pageToken=${nextPageToken}&key=${apiKey}`
          );
          const playlistData = await playlistRes.json();

          nextPageToken = playlistData.nextPageToken || "";

          const ids = playlistData.items
            .map((v) => v.snippet.resourceId.videoId)
            .join(",");

          const detailsRes = await fetch(
            `https://www.googleapis.com/youtube/v3/videos?part=contentDetails,snippet&id=${ids}&key=${apiKey}`
          );
          const detailsData = await detailsRes.json();

          const nonShorts = detailsData.items.filter((v) => {
            if (v.snippet.liveBroadcastContent !== "none") return false;

            const duration = v.contentDetails.duration;
            const hours = parseInt(duration.match(/(\d+)H/)?.[1] || 0);
            const minutes = parseInt(duration.match(/(\d+)M/)?.[1] || 0);
            const seconds = parseInt(duration.match(/(\d+)S/)?.[1] || 0);
            const totalSeconds = hours * 3600 + minutes * 60 + seconds;

            return totalSeconds > 60;
          });

          const idsOnly = nonShorts.map(v => v.id);
          collected = [...collected, ...idsOnly];

          safety++;
          if (!nextPageToken) break;
        }

        if (collected.length === 0) {
          setVideos(fallbackVideos);
          return;
        }

        setVideos(collected.slice(0, 3));
      } catch (err) {
        setVideos(fallbackVideos);
      }
    };

    fetchVideos();
  }, []);

  const slides =
    videos.length > 0
      ? [videos[videos.length - 1], ...videos, videos[0]]
      : [];

  const [index, setIndex] = useState(1);
  const [speed, setSpeed] = useState(".6s");

  const next = useCallback(() => setIndex((i) => i + 1), []);
  const prev = () => setIndex((i) => i - 1);

  useEffect(() => {
    if (slides.length === 0) return;
    const timer = setInterval(next, 4500);
    return () => clearInterval(timer);
  }, [next, slides.length]);

  useEffect(() => {
    if (slides.length === 0) return;

    if (index === slides.length - 1) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(1);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }

    if (index === 0) {
      setTimeout(() => {
        setSpeed("0s");
        setIndex(slides.length - 2);
        setTimeout(() => setSpeed(".6s"), 20);
      }, 600);
    }
  }, [index, slides.length]);

  return (
    <section className="carousel-section">
      <h2 className="carousel-title">Latest Videos</h2>

      <div className="simple-carousel">
        <button className="nav left" onClick={prev}>❮</button>

        <div className="track-wrapper">
          <div
            className="track"
            style={{
              transform: `translateX(-${index * 100}%)`,
              transition: speed
            }}
          >
            {slides.map((id, i) => (
  <div className="slide" key={i}>
    <Link
      to={`/videos/${id}`}
      className="thumb-link"
      state={{
        title: "Video",
        author: "Channel",
        date: ""
      }}
      style={{ cursor: "pointer" }}
    >
      <img
        src={`https://img.youtube.com/vi/${id}/hqdefault.jpg`}
        alt="video thumbnail"
        className="thumb-img"
      />
      <span className="play-overlay">▶</span>
    </Link>
  </div>
))}

          </div>
        </div>

        <button className="nav right" onClick={next}>❯</button>
      </div>
    </section>
  );
}
