import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { Container } from "react-bootstrap";
import PageTransition from "../PageTransition";
import WatchActions from "../components/WatchActions";

export default function SongWatch() {
  const { id } = useParams();

  const [song, setSong] = useState(null);
  const [ended, setEnded] = useState(false);

  const playerRef = useRef(null);
  const ytContainerRef = useRef(null);

  /* -------- FETCH SONG -------- */
  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/api/songs/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setSong(data);
        setEnded(false);
      });
  }, [id]);

  /* -------- LOAD YOUTUBE PLAYER -------- */
  useEffect(() => {
    if (!song) return;

    const createPlayer = () => {
      if (playerRef.current) return; // ðŸ”’ do not recreate

      playerRef.current = new window.YT.Player(ytContainerRef.current, {
        videoId: song.videoId,
        playerVars: {
          autoplay: 1,
          playsinline: 1,
          rel: 0,
          modestbranding: 1,
        },
        events: {
          onStateChange: (event) => {
            if (event.data === window.YT.PlayerState.ENDED) {
              setEnded(true); // hide player
            }
          },
        },
      });
    };

    if (!window.YT) {
      const tag = document.createElement("script");
      tag.src = "https://www.youtube.com/iframe_api";
      document.body.appendChild(tag);
      window.onYouTubeIframeAPIReady = createPlayer;
    } else {
      createPlayer();
    }
  }, [song]);

  /* -------- CLEANUP ON UNMOUNT -------- */
  useEffect(() => {
    return () => {
      if (playerRef.current?.destroy) {
        playerRef.current.destroy();
        playerRef.current = null;
      }
    };
  }, []);

  /* -------- REPLAY HANDLER -------- */
  const handleReplay = () => {
    if (!playerRef.current) return;

    setEnded(false);              // show player
    playerRef.current.seekTo(0);  // rewind
    playerRef.current.playVideo();
  };

  if (!song) return <h2 style={{ padding: 40 }}>Loading song...</h2>;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        {/* -------- PLAYER -------- */}
        <div
          className="video-wrapper"
          style={{ display: ended ? "none" : "block" }}
        >
          <div ref={ytContainerRef} />
        </div>

        {/* -------- REPLAY BUTTON -------- */}
        {ended && (
          <div style={{ textAlign: "center", marginTop: 20 }}>
            <button
              onClick={handleReplay}
              style={{
                padding: "10px 20px",
                fontSize: "16px",
                cursor: "pointer",
              }}
            >
              â–¶ Replay
            </button>
          </div>
        )}

        {/* -------- HEADER -------- */}
        <div className="watch-header">
          <h1 className="video-title">{song.title}</h1>

          <WatchActions
            itemId={song._id}
            type="song"
            videoUrl={`https://www.youtube.com/watch?v=${song.videoId}`}
          />
        </div>

        {/* -------- META -------- */}
        <div className="video-meta">
          <span>{song.host}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
