import React, { useEffect, useState, useRef } from "react";
import { useParams, useLocation } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function VideoWatch() {

  const { id } = useParams();
  const location = useLocation();
  const { title, author, date } = location.state || {};

  const shareRef = useRef();
  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("video-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("video-reactions", JSON.stringify(reactions));
  }, [reactions]);

  const handleLike = () =>
    setReactions(prev => ({ ...prev, [id]: prev[id] === "like" ? null : "like" }));

  const handleDislike = () =>
    setReactions(prev => ({ ...prev, [id]: prev[id] === "dislike" ? null : "dislike" }));

  const toggleShare = () => setOpenShare(prev => !prev);

  useEffect(() => {
    const handler = e => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  const videoUrl = `https://www.youtube.com/watch?v=${id}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>

        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${id}?autoplay=1`}
            title={title || "Video"}
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            allowFullScreen
          />
        </div>

        {/* ---- Title left | Buttons RIGHT ---- */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr auto",
            alignItems: "center",
            marginTop: "20px",
            gap: "10px"
          }}
        >
          <h1 className="video-title" style={{ margin: 0 }}>
            {title || "Video"}
          </h1>

          <div className="video-actions" style={{ justifyContent: "flex-end" }}>
            <button
              className={`action-btn ${reactions[id] === "like" ? "active like" : ""}`}
              onClick={handleLike}
            >
              üëç Like
            </button>

            <button
              className={`action-btn ${reactions[id] === "dislike" ? "active dislike" : ""}`}
              onClick={handleDislike}
            >
              üëé Dislike
            </button>

            <div className="share-container" ref={shareRef} style={{ position: "relative" }}>
              <button className="action-btn" onClick={toggleShare}>
                üîó Share
              </button>

              {openShare && (
                <div className="share-menu pro">

                  {/* WhatsApp */}
                  <a
                    href={`https://wa.me/?text=${encodeURIComponent(
                      (title || "Video") + " - " + videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <svg width="20" height="20" viewBox="0 0 32 32" fill="#25D366">
                      <path d="M16 .6C7.6.6.8 7.4.8 15.8c0 2.9.8 5.6 2.3 8L.7 31l7.4-2.3c2.3 1.2 4.9 1.9 7.6 1.9 8.4 0 15.2-6.8 15.2-15.2C30.9 7.4 24.1.6 16 .6zm8.8 21.8c-.4 1.2-2.2 2.2-3.1 2.2-.8 0-1.7.5-5.7-1.2-4.8-2-7.8-6.7-8-7-.2-.3-1.9-2.6-1.9-5s1.2-3.6 1.7-4.1c.4-.5.9-.6 1.2-.6h.9c.3 0 .7 0 1 .8.4 1 .9 2.4 1 2.6.1.2.2.5 0 .8-.2.4-.3.5-.6.9-.3.3-.5.5-.2 1 .3.5 1.4 2.3 3 3.7 2.1 1.9 3.7 2.5 4.2 2.8.4.2.7.2 1-.1.3-.3 1.2-1.4 1.5-1.9.3-.4.6-.4 1-.2.4.2 2.6 1.3 3 1.5.4.2.7.3.8.5.1.4.1 1.3-.3 2.4z"/>
                    </svg>
                    WhatsApp
                  </a>

                  {/* X */}
                  <a
                    href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(
                      videoUrl
                    )}&text=${encodeURIComponent(title || "Video")}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="white">
                      <path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.34l-5.214-6.823L4.99 21.75H1.68l7.73-8.835L1.254 2.25H7.91l4.713 6.231 5.62-6.231z"/>
                    </svg>
                    Twitter / X
                  </a>

                  {/* Facebook */}
                  <a
                    href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(
                      videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="#1877F2">
                      <path d="M22.675 0H1.325C.593 0 0 .593 0 1.326v21.348C0 23.407.593 24 1.325 24H12.82v-9.294H9.692v-3.622h3.128V8.413c0-3.1 1.893-4.788 4.66-4.788 1.325 0 2.463.099 2.795.143v3.24h-1.918c-1.504 0-1.796.715-1.796 1.762v2.31h3.587l-.467 3.622H16.56V24h6.115c.73 0 1.325-.593 1.325-1.326V1.326C24 .593 23.407 0 22.675 0z"/>
                    </svg>
                    Facebook
                  </a>

                  <button onClick={() => navigator.clipboard.writeText(videoUrl)}>
                    üìã Copy Link
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="video-meta">
          <span>{author || "Channel"}</span>
          <span>‚Äî</span>
          <span>{date || ""}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
