import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function PodcastWatch() {
  const { id } = useParams();

  const podcasts = [
    {
      id: 1,
      embed: "https://www.youtube.com/embed/UobU3mvxat4",
      title: "à®®à¯à®Ÿà®¿à®šà¯à®šà®¿à®Ÿà®²à®¾à®®à®¾ðŸ”¥à®¤à®¿à®®à¯à®•à®µà¯ˆ à®®à¯Šà®¤à¯à®¤à®®à®¾ à®®à¯à®Ÿà®¿à®šà¯à®šà®¿à®Ÿà®²à®¾à®®à¯ðŸ‘Š",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    // ... keep rest as-is
  ];

  const video = podcasts.find((v) => v.id === Number(id));

  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);
  const shareRef = useRef();

  useEffect(() => {
    const saved = localStorage.getItem("podcast-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("podcast-reactions", JSON.stringify(reactions));
  }, [reactions]);

  const handleLike = () =>
    setReactions((p) => ({ ...p, [id]: p[id] === "like" ? null : "like" }));

  const handleDislike = () =>
    setReactions((p) => ({ ...p, [id]: p[id] === "dislike" ? null : "dislike" }));

  const toggleShare = () => setOpenShare((p) => !p);

  useEffect(() => {
    const handler = (e) => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  if (!video) return <h2 style={{ padding: 40 }}>Podcast not found</h2>;

  const videoId = video.embed.split("/embed/")[1]?.split("?")[0];
  const videoUrl = `https://www.youtube.com/watch?v=${videoId}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        <div className="video-wrapper">
          <iframe
            src={`https://www.youtube.com/embed/${videoId}`}
            title={video.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>

        <h1 className="video-title">{video.title}</h1>

        <div className="video-meta">
          <span>{video.author}</span>
          <span>â€”</span>
          <span>{video.date}</span>
        </div>
      </Container>
    </PageTransition>
  );
}
