import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import PageTransition from "../PageTransition";
import { Container } from "react-bootstrap";

export default function SongWatch() {

  const { id } = useParams();

  const videos = [
    {
      id: 1,
      embed: "https://www.youtube.com/embed/up4TuGdM5SQ",
      title:
        "THIRUTTU OOZHAL ‡Æ§‡Æø‡ÆÆ‡ØÅ‡Æï - ‡ÆÆ‡Æï‡Øç‡Æï‡Æ≥‡Øà ‡Æ™‡Æ±‡Øç‡Æ±‡Æø‡Æ© ü§î ‡Æ®‡Æø‡ÆØ‡Ææ‡Æ™‡Æï‡ÆÆ‡Øç ‡Æá‡Æ∞‡ØÅ‡Æï‡Øç‡Æï‡Ææ üòÇ RAP SONG | DMK SPOOF",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 2,
      embed: "https://www.youtube-nocookie.com/embed/0hjqWmnUDWo",
      title:
        "APPAN PULLA ROMBA THOLLA | ENGEY NIMMATHI DMK VERSION | DMK TROLL SONG | MK STALIN",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 3,
      embed: "https://www.youtube-nocookie.com/embed/yvhI98yGzMg",
      title:
        "‡Æ§‡Æø‡ÆÆ‡ØÅ‡Æï ‡Æâ‡Æ∞‡ØÅ‡Æü‡Øç‡Æü‡Ææ‡Æ§ ‡Æ®‡Ææ‡Æ≥‡Æø‡Æ≤‡Øç‡Æ≤‡Øà‡ÆØ‡Øá..üòÇ| AMMA ENDRAZHAIKKADHA SPOOF SONG | MK STALIN | RAJINI | ILAIYARAAJA",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 4,
      embed: "https://www.youtube-nocookie.com/embed/-mjCAtjN0MM",
      title:
        "#ByeByeStalin - DMK Troll Music Video Ft. MK Stalin ‡Æä‡Æ¥‡Æ≤‡Øç | Udhayanidhi | Theemuka Theeyasakthi",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 5,
      embed: "https://www.youtube-nocookie.com/embed/y8WUI2ZmTlw",
      title:
        "‡Æµ‡Ææ‡Æü‡Æø ‡Æ™‡Øä‡Æü‡Øç‡Æü ‡Æ™‡ØÅ‡Æ≥‡Øç‡Æ≥ ‡Æµ‡ØÜ‡Æ≥‡Æø‡ÆØ.. ‡Æ™‡Øä‡ÆÆ‡Øç‡ÆÆ‡Øà CM SONG..üòÇ | MK STALIN | VADIVELU | UDHAYANIDHI | DMK SONG",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 6,
      embed: "https://www.youtube-nocookie.com/embed/ijFoj04uBX0",
      title:
        "‡Æé‡Æ™‡Øç‡Æ™‡Æü‡Ææ..‡Æ®‡Æ≤‡Øç‡Æ≤‡Ææ‡Æü‡Øç‡Æö‡Æø ‡Æµ‡Æ∞‡ØÅ‡ÆÆ‡Øç..ü§î| #IDLYKADAI | DMK VERSION | MK STALIN | DHANUSH",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
    {
      id: 7,
      embed: "https://www.youtube-nocookie.com/embed/z8sBAcNX7ag",
      title:
        "‡Æï‡Ææ‡Æ±‡Øç‡Æ±‡Æø‡Æ≤‡Øç '‡Æä‡Æ¥‡Æ≤‡Øç' ‡ÆÜ‡Æ±‡Øç‡Æ±‡Æø‡Æ≤‡Øç '‡Æï‡Øä‡Æ≥‡Øç‡Æ≥‡Øà' New Song | DMK | MK Stalin | Udhayanidhi",
      author: "DMK TheeyaSakthi Admin",
      date: "November 3, 2025",
    },
  ];

  const video = videos.find(v => v.id === Number(id));

  const shareRef = useRef();

  const [reactions, setReactions] = useState({});
  const [openShare, setOpenShare] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("song-reactions");
    if (saved) setReactions(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("song-reactions", JSON.stringify(reactions));
  }, [reactions]);

  const handleLike = () =>
    setReactions(prev => ({ ...prev, [id]: prev[id] === "like" ? null : "like" }));

  const handleDislike = () =>
    setReactions(prev => ({ ...prev, [id]: prev[id] === "dislike" ? null : "dislike" }));

  const toggleShare = () => setOpenShare(prev => !prev);

  useEffect(() => {
    const handler = e => {
      if (shareRef.current && !shareRef.current.contains(e.target)) {
        setOpenShare(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  if (!video) return <h2 style={{ padding: 40 }}>Video not found</h2>;

  const videoId = video.embed.split("/embed/")[1]?.split("?")[0];
  const videoUrl = `https://www.youtube.com/watch?v=${videoId}`;

  return (
    <PageTransition>
      <Container style={{ marginTop: "30px" }}>
        <div className="video-wrapper">
          <iframe
            src={`${video.embed}?autoplay=1`}
            title={video.title}
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            allowFullScreen
          />
        </div>

        {/* ---- Title left | Buttons RIGHT ---- */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr auto",
            alignItems: "center",
            marginTop: "20px",
            gap: "10px"
          }}
        >
          <h1 className="video-title" style={{ margin: 0 }}>
            {video.title}
          </h1>

          <div className="video-actions" style={{ justifyContent: "flex-end" }}>
            <button
              className={`action-btn ${reactions[id] === "like" ? "active like" : ""}`}
              onClick={handleLike}
            >
              üëç Like
            </button>

            <button
              className={`action-btn ${reactions[id] === "dislike" ? "active dislike" : ""}`}
              onClick={handleDislike}
            >
              üëé Dislike
            </button>

            <div className="share-container" ref={shareRef} style={{ position: "relative" }}>
              <button className="action-btn" onClick={toggleShare}>
                üîó Share
              </button>

              {openShare && (
                <div className="share-menu pro">

                  {/* WhatsApp */}
                  <a
                    href={`https://wa.me/?text=${encodeURIComponent(
                      video.title + " - " + videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <svg width="20" height="20" viewBox="0 0 32 32" fill="#25D366">
                      <path d="M16 .6C7.6.6.8 7.4.8 15.8c0 2.9.8 5.6 2.3 8L.7 31l7.4-2.3c2.3 1.2 4.9 1.9 7.6 1.9 8.4 0 15.2-6.8 15.2-15.2C30.9 7.4 24.1.6 16 .6zm8.8 21.8c-.4 1.2-2.2 2.2-3.1 2.2-.8 0-1.7.5-5.7-1.2-4.8-2-7.8-6.7-8-7-.2-.3-1.9-2.6-1.9-5s1.2-3.6 1.7-4.1c.4-.5.9-.6 1.2-.6h.9c.3 0 .7 0 1 .8.4 1 .9 2.4 1 2.6.1.2.2.5 0 .8-.2.4-.3.5-.6.9-.3.3-.5.5-.2 1 .3.5 1.4 2.3 3 3.7 2.1 1.9 3.7 2.5 4.2 2.8.4.2.7.2 1-.1.3-.3 1.2-1.4 1.5-1.9.3-.4.6-.4 1-.2.4.2 2.6 1.3 3 1.5.4.2.7.3.8.5.1.4.1 1.3-.3 2.4z"/>
                    </svg>
                    WhatsApp
                  </a>

                  {/* Twitter / X */}
                  <a
                    href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(
                      videoUrl
                    )}&text=${encodeURIComponent(video.title)}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="white">
                      <path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.34l-5.214-6.823L4.99 21.75H1.68l7.73-8.835L1.254 2.25H7.91l4.713 6.231 5.62-6.231z"/>
                    </svg>
                    Twitter / X
                  </a>

                  {/* Facebook */}
                  <a
                    href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(
                      videoUrl
                    )}`}
                    target="_blank"
                    rel="noreferrer"
                  >
